export default {
  name: "Swiper",
  props: {
    interval: {
      type: Number,
      default: 3000
    },
    animDuration: {
      type: Number,
      default: 300
    },
    moveRatio: {
      type: Number,
      default: 0.25
    },
    showIndicator: {
      type: Boolean,
      default: true
    }
  },
  data: function data() {
    return {
      slideCount: 0,
      // 元素个数
      totalWidth: 0,
      // swiper的宽度
      swiperStyle: {},
      // swiper样式
      currentIndex: 1,
      // 当前的index
      scrolling: false // 是否正在滚动

    };
  },
  mounted: function mounted() {
    var _this = this;

    // 1.操作DOM, 在前后添加Slide
    setTimeout(function () {
      _this.handleDom(); // 2.开启定时器


      _this.startTimer();
    }, 100);
  },
  methods: {
    /**
       * 定时器操作
       */
    startTimer: function startTimer() {
      var _this2 = this;

      this.playTimer = window.setInterval(function () {
        _this2.currentIndex++;

        _this2.scrollContent(-_this2.currentIndex * _this2.totalWidth);
      }, this.interval);
    },
    stopTimer: function stopTimer() {
      window.clearInterval(this.playTimer);
    },

    /**
     * 滚动到正确的位置
     */
    scrollContent: function scrollContent(currentPosition) {
      // 0.设置正在滚动
      this.scrolling = true; // 1.开始滚动动画

      this.swiperStyle.transition = 'transform ' + this.animDuration + 'ms';
      this.setTransform(currentPosition); // 2.判断滚动到的位置

      this.checkPosition(); // 4.滚动完成

      this.scrolling = false;
    },

    /**
     * 校验正确的位置
     */
    checkPosition: function checkPosition() {
      var _this3 = this;

      window.setTimeout(function () {
        // 1.校验正确的位置
        _this3.swiperStyle.transition = '0ms';

        if (_this3.currentIndex >= _this3.slideCount + 1) {
          _this3.currentIndex = 1;

          _this3.setTransform(-_this3.currentIndex * _this3.totalWidth);
        } else if (_this3.currentIndex <= 0) {
          _this3.currentIndex = _this3.slideCount;

          _this3.setTransform(-_this3.currentIndex * _this3.totalWidth);
        } // 2.结束移动后的回调


        _this3.$emit('transitionEnd', _this3.currentIndex - 1);
      }, this.animDuration);
    },

    /**
     * 设置滚动的位置
     */
    setTransform: function setTransform(position) {
      this.swiperStyle.transform = "translate3d(".concat(position, "px, 0, 0)");
      this.swiperStyle['-webkit-transform'] = "translate3d(".concat(position, "px), 0, 0");
      this.swiperStyle['-ms-transform'] = "translate3d(".concat(position, "px), 0, 0");
    },

    /**
     * 操作DOM, 在DOM前后添加Slide
     */
    handleDom: function handleDom() {
      // 1.获取要操作的元素
      var swiperEl = document.querySelector('.swiper');
      var slidesEls = swiperEl.getElementsByClassName('slide'); // 2.保存个数

      this.slideCount = slidesEls.length; // 3.如果大于1个, 那么在前后分别添加一个slide

      if (this.slideCount > 1) {
        var cloneFirst = slidesEls[0].cloneNode(true);
        var cloneLast = slidesEls[this.slideCount - 1].cloneNode(true);
        swiperEl.insertBefore(cloneLast, slidesEls[0]);
        swiperEl.appendChild(cloneFirst);
        this.totalWidth = swiperEl.offsetWidth;
        this.swiperStyle = swiperEl.style;
      } // 4.让swiper元素, 显示第一个(目前是显示前面添加的最后一个元素)


      this.setTransform(-this.totalWidth);
    },

    /**
     * 拖动事件的处理
     */
    touchStart: function touchStart(e) {
      // 1.如果正在滚动, 不可以拖动
      if (this.scrolling) return; // 2.停止定时器

      this.stopTimer(); // 3.保存开始滚动的位置

      this.startX = e.touches[0].pageX;
    },
    touchMove: function touchMove(e) {
      // 1.计算出用户拖动的距离
      this.currentX = e.touches[0].pageX;
      this.distance = this.currentX - this.startX;
      var currentPosition = -this.currentIndex * this.totalWidth;
      var moveDistance = this.distance + currentPosition; // 2.设置当前的位置

      this.setTransform(moveDistance);
    },
    touchEnd: function touchEnd(e) {
      // 1.获取移动的距离
      var currentMove = Math.abs(this.distance); // 2.判断最终的距离

      if (this.distance === 0) {
        return;
      } else if (this.distance > 0 && currentMove > this.totalWidth * this.moveRatio) {
        // 右边移动超过0.5
        this.currentIndex--;
      } else if (this.distance < 0 && currentMove > this.totalWidth * this.moveRatio) {
        // 向左移动超过0.5
        this.currentIndex++;
      } // 3.移动到正确的位置


      this.scrollContent(-this.currentIndex * this.totalWidth); // 4.移动完成后重新开启定时器

      this.startTimer();
    },

    /**
     * 控制上一个, 下一个
     */
    previous: function previous() {
      this.changeItem(-1);
    },
    next: function next() {
      this.changeItem(1);
    },
    changeItem: function changeItem(num) {
      // 1.移除定时器
      this.stopTimer(); // 2.修改index和位置

      this.currentIndex += num;
      this.scrollContent(-this.currentIndex * this.totalWidth); // 3.添加定时器

      this.startTimer();
    }
  }
};
